import { useContext } from 'react';
import AppContext from './AppContext';

const useAudioPlayer = () => {
  const { value, update } = useContext(AppContext);

  const play = () => {
    update({ ...value, isPlaying: true });
  };

  const pause = () => {
    update({ ...value, isPlaying: false });
  };

  return {
    isPlaying: value,
    play,
    pause,
  }
};

export default useAudioPlayer;
