import { useContext } from 'react';
import PlayingContext from './AppContext';

const useAudioPlayer = () => {
  const { value, update } = useContext(PlayingContext);
  const play = () => {
    update(true);
  };

  const pause = () => {
    update(false);
  };

  return {
    isPlaying: value,
    play,
    pause,
  }
};

export default useAudioPlayer;
