import { useState } from 'react';

const useAudioPlayer = () => {
  const [isPlaying, setIsPlaying] = useState(false);

  const play = () => {
    setIsPlaying(true);
  };

  const pause = () => {
    setIsPlaying(false);
  };

  return {
    isPlaying,
    play,
    pause,
  }
};

export default useCounter;
