import { useState } from 'react';

const useCounter = () => {
  const [counter, setCounter] = useState(0);

  const incrementCounter = () => {
    if (counter === 5) {
      alert('Five!');
    }
    setCounter(counter + 1);
  };

  return {
    counter,
    incrementCounter,
  }
};

export default useCounter;
