import React, { useEffect } from 'react';
import SongList from './SongList';
import useAudioPlayer from "./useCounter";

const App = () => {
  const { isPlaying, play, pause } = useAudioPlayer();

  useEffect(() => {
    console.log('Hello first load!');
  }, [isPlaying]);

  return (
    <>
      <h1>
        {isPlaying ? 'Song playing!' : 'song not playing!'}
      </h1>
      <button onClick={() => play()}>Play song!</button>
      <button onClick={() => pause()}>Pause song!</button>
      <SongList />
    </>
  );
};

export default App;
